css.ghostAppend:
/* ===== GHOST FIX LAYER (CSS-only) ===== */
html,body{overflow-x:hidden!important}

/* NAV: keep layout stable; hide duplicate brand on mobile */
.nav-header{position:sticky;top:0;left:0;right:0;width:100%;z-index:1000;background:var(--nav-background,#080808)!important}
.nav-content{max-width:1200px;margin:0 auto;display:flex;align-items:center;justify-content:space-between!important;padding:8px 16px}
@media(max-width:768px){header.nav-header img.nav-brand+img.nav-brand{display:none!important}}

/* HERO: desktop uses <img>; mobile paints background (correct relative path!) */
.hero-section{min-height:92vh;display:flex;align-items:center;justify-content:center;padding:0;background:#000}
.hero-image{display:block;max-width:min(980px,92vw);width:100%;height:auto;margin:0 auto;filter:drop-shadow(0 0 10px rgba(185,255,99,.45))}
@media(max-width:768px){
  .container{padding:0 16px}
  .section{padding:48px 0}
  h1{font-size:clamp(1.6rem,7vw,2.2rem)}
  h2{font-size:clamp(1.3rem,6vw,1.9rem)}
  p{font-size:1rem;line-height:1.55}
  /* key fix: path is relative to assets/css/_ghost.css -> use ../img/ */
  .hero-section{min-height:68vh;padding:8px 0;background:#000 url('../img/testghost_dark_alt.png') center / cover no-repeat!important}
  .hero-image{display:none!important}
  .scroll-down-btn{opacity:1;bottom:14px}
}

/* PRICING polish (kept) */
.pricing-accordion{max-width:860px;margin:0 auto;display:flex;flex-direction:column;gap:18px}
.pricing-item{border-radius:14px;background:linear-gradient(180deg,rgba(255,255,255,.035),rgba(255,255,255,.02));box-shadow:0 10px 28px rgba(0,0,0,.25),0 0 0 1px rgba(0,255,136,.18) inset;overflow:hidden;transition:box-shadow .25s ease,transform .2s ease}
.pricing-item:hover{transform:translateY(-1px);box-shadow:0 16px 36px rgba(0,0,0,.32),0 0 0 1px rgba(0,255,136,.24) inset}
.pricing-item summary{cursor:pointer;list-style:none;display:grid;grid-template-columns:1fr auto auto;align-items:center;gap:16px;padding:1.05rem 1.3rem;color:var(--accent,#00ff88);font-weight:600}
.pricing-item summary::-webkit-details-marker{display:none}
.pricing-item summary .summary-price{font-weight:700;letter-spacing:.01em;color:#a3ffcd}
.pricing-item summary .summary-plus{font-size:1.4rem;line-height:1;opacity:.9;transition:transform .25s ease}
.pricing-item[open] summary{background:rgba(0,255,136,.08)}
.pricing-item[open] summary .summary-plus{transform:rotate(45deg)}
.pricing-body{padding:.3rem 1.3rem 1.1rem}
.pricing-body ul{margin:.6rem 0 0;padding-left:1rem;display:grid;grid-template-columns:1fr 1fr;gap:.45rem 1.2rem}
.pricing-body li{opacity:.95}
@media(max-width:768px){.pricing-body ul{grid-template-columns:1fr}}

/* FOOTER: full-bleed, centered content */
footer{position:static!important;margin:0!important;width:100%!important;background:#222!important;color:#aaa;padding:24px 0;border-top:1px solid #444;box-sizing:border-box}
.footer-logo,.footer-text{max-width:1200px;margin:0 auto;padding:0 24px}
