css.ghostAppend:
/* ===== GHOST DEMO: NAV + FOOTER OMBRE ===== */

/* Nav gradient */
.nav-header {
  background: linear-gradient(to bottom, #000000, #1a1a1a) !important;
}

/* Footer gradient */
.site-footer {
  background: linear-gradient(to top, #000000, #1a1a1a) !important;
}

/* Optional: make text stand out */
.nav-header, .site-footer {
  color: #f5f5f5;
}
.nav-header a, .site-footer a {
  color: #b9ff63;
  text-decoration: none;
}
